snippet.code:
/**
 * Gets the login status.
 *
 * @return     string  The login status.
 */
function get_login_status() {
	if ( is_user_logged_in() ) {
		global $current_user;
		wp_get_current_user();
		return '<ul id="mem" class="clearfix"><li><a href="/my-account/">Hi ' . $current_user->display_name . '</a> | <a href="/my-account/customer-logout/">Logout</a></li></ul>';
	} else {
		return '<ul id="mem" class="clearfix"><li><a href="/my-account/">Register</a> | <a href="/my-account/">Login</a></li></ul>';
	}
}
add_shortcode( 'login-status', 'get_login_status' );
