add_action( 'woocommerce_single_product_summary', 'display_color_attribute', 5 );
function display_color_attribute() {
    global $product;

    if ( $product->is_type('variable') ) {
        $taxonomy = 'pa_color';
        echo '<span class="attribute-size">' . $product->get_attribute($taxonomy) . '</span>';
    }
}
