function wc_free_custom_notice() {
	if ( ! is_cart() ) {
		return;
	}
	$subtotal = WC()->cart->cart_contents_total + WC()->cart->get_taxes_total( false, false );
	$free_shipping_threshold = 50;
	if ( $subtotal < $free_shipping_threshold && $subtotal > ( $free_shipping_threshold / 2 ) ) {
		wc_add_notice( 'Orders above $50 get free shipping', 'notice' );
	}
}
add_action( 'wp', 'wc_free_custom_notice' );
