add_action( 'woocommerce_before_add_to_cart_form', 'wc_woocommerce_template_product_description', 12 );
/**
* Add product description after price
* Output description tab template using 'woocommerce_before_single_product' hook
*/
function wc_woocommerce_template_product_description() {
	wc_get_template( 'single-product/tabs/description.php' );
}
add_filter( 'woocommerce_product_tabs', 'wc_woo_remove_product_tabs', 98 );
/**
* Remove WooCommerce Description Tab
*/
function wc_woo_remove_product_tabs( $tabs ) {
    unset( $tabs['description'] );      	// Remove the description tab
    
    return $tabs;
}
