function wc_rename_coupon_message_on_checkout() {
 return 'Have a Promo Code?' . ' <a href="#" class="showcoupon">' . __( 'Click here to enter your code', 'woocommerce' ) . '</a>';
}
add_filter( 'woocommerce_checkout_coupon_message', 'wc_rename_coupon_message_on_checkout' );
// rename the coupon field on the checkout page
function wc_rename_coupon_field_on_checkout( $translated_text, $text, $text_domain ) {
 // bail if not modifying frontend woocommerce text
 if ( is_admin() || 'woocommerce' !== $text_domain ) {
  return $translated_text;
 }
 if ( 'Coupon code' === $text ) {
  $translated_text = 'Promo Code';
 
 } elseif ( 'Apply Coupon' === $text ) {
  $translated_text = 'Apply Promo Code';
 }
 return $translated_text;
}
add_filter( 'gettext', 'wc_rename_coupon_field_on_checkout', 10, 3 );
