function wc_empty_cart_notice() {
    
	if ( WC()->cart->get_cart_contents_count() == 0 ) {
        	wc_print_notice( __( 'Get free shipping if your order is over &#36;60!', 'woocommerce' ), 'notice' );
        	// Change notice text as desired
	}

}
// Add to cart page
add_action( 'woocommerce_check_cart_items', 'wc_empty_cart_notice' );
// Add to shop archives & product pages
add_action( 'woocommerce_before_main_content', 'wc_empty_cart_notice' );
