function wp_update_privacy( $query ) {
	unset($query['php']);
	unset($query['mysql']);
	unset($query['local_package']);
	unset($query['blogs']);
	unset($query['users']);
	unset($query['multisite_enabled']);
	unset($query['initial_db_version']);
	return $query;
}
add_action( 'core_version_check_query_args', 'wp_update_privacy' );
