add_filter( 'woocommerce_package_rates', 'woocommerce_tiered_shipping', 10, 2 );
   
function woocommerce_tiered_shipping( $rates, $package ) {
  $threshold = 41.99;
    if ( WC()->cart->subtotal < $threshold ) {
        if ( isset( $rates['flat_rate:8'] ) ) unset( $rates['flat_rate:9'] );
    } else {
        if ( isset( $rates['flat_rate:9'] ) ) unset( $rates['flat_rate:8'] );
    }
  return $rates;
}
